function zoom(b){var d=(document.all)?document.all.zoom:document.getElementById("zoom");var a=(document.compatMode&&document.compatMode!="BackCompat")?document.documentElement:document.body;var c=(document.all)?a.scrollTop:pageYOffset;d.style.top=(c+75)+"px";document.getElementById("zoom").style.display="block";document.getElementById("closeup").src=b}function unzoom(a){document.getElementById(a).style.display="none";document.getElementById("closeup").src="pics/1pixel.gif"}var prev="home";function show(a){if(prev!=a&&document.getElementById(prev)){document.getElementById(prev).style.display="none";document.getElementById("nav"+prev).className=""}if(prev==a){prev=0}else{if(document.getElementById(a)){document.getElementById(a).style.display="block";document.getElementById("nav"+a).className="selected";prev=a}}};